const contactsLink=document.querySelector(".contacts-link");const mailLink=document.querySelector(".pop-up");const popupClose=mailLink.querySelector(".button-close");contactsLink.addEventListener("click",function(a){a.preventDefault();mailLink.classList.add("pop-up-show")});popupClose.addEventListener("click",function(a){a.preventDefault();mailLink.classList.remove("pop-up-show")});