const contactsLink=document.querySelector(".contacts-link");const mailLink=document.querySelector(".pop-up");const popupClose=mailLink.querySelector(".button-close");const contactForm=mailLink.querySelector("form");const loginField=mailLink.querySelector("#meeting-name");const mailField=mailLink.querySelector("#email-login");contactForm.addEventListener("submit",function(a){if(!loginField.value||!mailField.value){a.preventDefault();mailLink.classList.add("modal-error");console.log("error")}});contactsLink.addEventListener("click",function(a){a.preventDefault();mailLink.classList.add("pop-up-show")});popupClose.addEventListener("click",function(a){a.preventDefault();mailLink.classList.remove("pop-up-show");mailLink.classList.remove("modal-error")});window.addEventListener("keydown",function(a){if(a.keyCode===27){if(mailLink.classList.contains("pop-up-show")){a.preventDefault();mailLink.classList.remove("pop-up-show")}}});